import { useState } from "react"

function App(){


      const [deg,setdeg] = useState("0")
      const [city,setcity] = useState("France")
      const [desc,setdesc] = useState("Rainy")


    return(
      <div className="flex flex-row justify-center h-[100vh] items-center">
        <div style={{"background-image": "linear-gradient(120deg, #a6c0fe 0%, #f68084 100%)"}}
        className="p-2 rounded-md shadow" >
          <h2 className="font-medium">Hey !</h2>
          <p className="text-xs">Do you want to know the Weather Report :)</p>
          <input className="rounded-md h-6 text-sm mt-2 p-1 outline-none" placeholder="City Name"></input>
          <br></br>
          <button className="bg-black text-white rounded-md p-1 mt-2 text-xs">Get Report</button>
          <p className="mt-2 text-xs">Degree: {deg} | City: {city}| Rainy</p>
        </div>
      </div>
    )
  }

export default App
